import React, { useState, useEffect }from 'react';
import './global.css';
import './app.css';
import './sidebar.css';
import './main.css';

//componente = função que retorna qualquer conteudo 
//estado =  atributo para componentes (ex.: title) )( informações que um componente 
//pai passa para o filho
//propriedade = informação que o componente vai manipular )
//( informações mantidas pelo componente (lembrar imutabilidade)



function App(){ //componente pai

  const [github_username, setGithubusername] = useState('');
  const [techs, setTechs] = useState('');  

  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');

  useEffect(()=>{
    navigator.geolocation.getCurrentPosition(
      (position)=>{
        const {latitude, longitude}= position.coords;

        setLatitude(latitude);
        setLongitude(longitude);
      },
      (err)=> {
          console.log(err);
      },
      {
        timeout:30000,
      }
    )
  }, []);


  return(
    <div id="app"> 
      <aside>
        <strong>Cadastrar</strong>
        <form>
          <div className="input-block">
            <label htmlFor="github_username">Usuário do Github</label>
            <input 
            name="github_username"
            id="github_username"
            required
            value={github_username}
            onChange={e =>setGithubusername(e.target.value)}
            />
          </div>

          <div className="input-block">
            <label htmlFor="techs">Tecnologias</label>
            <input 
            name="techs"
            id="techs"
            required
            value={techs}
            onChange={e =>setTechs(e.target.value)}
            />
          </div>

          <div className="input-group">
            <div className="input-block">
              <label htmlFor="latitude">Latitude</label>
              <input
               type="number" 
               name="latitude" 
               id="latitude"
               required 
               value={latitude} 
               onChange={e => setLatitude(e.target.value)}
               />
            </div>

            <div className="input-block">
              <label htmlFor="longitude">Longitude</label>
              <input 
              type="number"
              name="longitude" 
              id="longitude"
              required
              value={longitude}
              onChange={e=> setLongitude(e.target.value)}
              />
            </div>
          </div>

          <button type="submit">Salvar</button>

        </form>
      </aside>



      <main>

        <ul>
          <li className="dev-item">
              <header>
                <img src="https://avatars0.githubusercontent.com/u/38856265?s=460&v=4" alt="Alef David"/>
                <div className="user-info">
                  <strong>Alef David</strong>
                  <span>ReactJS, React Native, Node.JS</span>
                </div>
              </header> 
              <p>Desenvolvedor Full-Stack</p>
              <a href="https://github.com/alefdav">Acessar perfil no Github</a>
          </li>

          <li className="dev-item">
              <header>
                <img src="https://avatars0.githubusercontent.com/u/38856265?s=460&v=4" alt="Alef David"/>
                <div className="user-info">
                  <strong>Alef David</strong>
                  <span>ReactJS, React Native, Node.JS</span>
                </div>
              </header> 
              <p>Desenvolvedor Full-Stack</p>
              <a href="https://github.com/alefdav">Acessar perfil no Github</a>
          </li>

          <li className="dev-item">
              <header>
                <img src="https://avatars0.githubusercontent.com/u/38856265?s=460&v=4" alt="Alef David"/>
                <div className="user-info">
                  <strong>Alef David</strong>
                  <span>ReactJS, React Native, Node.JS</span>
                </div>
              </header> 
              <p>Desenvolvedor Full-Stack</p>
              <a href="https://github.com/alefdav">Acessar perfil no Github</a>
          </li>

          <li className="dev-item">
              <header>
                <img src="https://avatars0.githubusercontent.com/u/38856265?s=460&v=4" alt="Alef David"/>
                <div className="user-info">
                  <strong>Alef David</strong>
                  <span>ReactJS, React Native, Node.JS</span>
                </div>
              </header> 
              <p>Desenvolvedor Full-Stack</p>
              <a href="https://github.com/alefdav">Acessar perfil no Github</a>
          </li>


        </ul>

      </main>
    </div>
  );
}

export default App;
